import React from "react"



export default class StatOptions extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            values: []
        }

        this.handleClick = this.handleClick.bind(this);
    }

    handleClick() {
        this.setState(state => ({
            values: state.values.push(this.val)
        }))
    }

    render() {
        return (
            <div id="statOptions">
                <table>
                    <tbody>
                        <tr>
                            <td><input type="checkbox" value ="Salary" onClick={handleClick}/>Salary</td>
                            <td><input type="checkbox" value ="Points" onClick={handleClick} />Points</td>
                            <td><input type="checkbox" value ="Steals" onClick={handleClick}/>Steals</td>
                            <td><input type="checkbox" value ="FG%" onClick={handleClick}/>FG%</td>
                        </tr>
                        <tr>
                            <td><input type="checkbox" value ="Rating" onClick={handleClick}/>Ranking</td>
                            <td><input type="checkbox" value ="Rebounds" onClick={handleClick}/>Rebounds</td>
                            <td><input type="checkbox" value ="Blocks" onClick={handleClick}/>Blocks</td>
                            <td><input type="checkbox" value ="3-PT%" onClick={handleClick}/>3-PT%</td>
                        </tr>
                        <tr>
                            <td><input type="checkbox" value ="Rank" onClick={handleClick}/>Rank</td>
                            <td><input type="checkbox" value ="Assists" onClick={handleClick}/>Assists</td>
                            <td><input type="checkbox" value ="Turnovers" onClick={handleClick}/>Turnovers</td>
                            <td><input type="checkbox" value ="FT%" onClick={handleClick}/>FT%</td>
                        </tr>
                    </tbody>
                </table>
                <p>{values}</p>
            </div>
        )
    } 
}
