import React from "react"
import Plot from 'react-plotly.js'

class Graph extends React.Component {
    constructor(props) {
        super(props);
        this.data = props.data;
    }

    render() {
        switch(this.props.graphType) {
            case "pieStyle":
                var data = [{
                values: [19, 26, 55],
                labels: ['Residential', 'Non-Residential', 'Utility'],
                type: 'pie'
                }];

                var layout = {
                height: 400,
                width: 500
                };

                Plotly.newPlot('myDiv', data, layout);

            case "barStyle":
                return(
                    <Plot 
                        data={[
                            {
                            type: 'bar',
                            x: [1, 2, 3],
                            y: [2, 5, 3]
                            }
                        ]}
                    />
                )
//            case "lineStyle":

            default:
                return(
                    <Plot 
                        data={[
                            {
                            x: [1, 2, 4],
                            y: [2, 6, 3],
                            type: 'scatter',
                            mode: 'lines+points',
                            marker: {color: 'red'},
                            }
                        ]}
                    />
                )
        }

    }
}

export default Graph
