import React from "react"
import ReactTable from "react-table";
import Firebase from "./firebase"
import "react-table/react-table.css"

require("firebase");

const firebase_client = new Firebase();

let player_names = firebase_client.db.ref("players");


var names = ["James Harden", "Russel Westbrook", "Anthony Davis", "LeBron James", "Victor Oladipo", "Paul George", "Ben Simmons", "John Wall"]
var players = [];


class player {
constructor() {
this.name = "";
this.points = 0;
this.rebounds = 0;
this.assists = 0;
this.steals = 0;
this.blocks = 0;
this.fieldGoalPercentage = 0;
this.threePointPercentage = 0;
this.freeThrowPercentage = 0;
this.turnovers = 0;
} 
}


player_names.on('value', (snapshot) => {
    if(snapshot.exists()) {
        console.log(snapshot)
        snapshot.forEach((data) => {
        var val = data.val();
//        console.log(val);
        players.push({
            name: val.name,
            ppg: val.ppg,
            reb: val.reb,
            ast: val.ast,
            //console.log(new_player.assists)
            stl: val.stl,
            blk: val.blk,
            fg: val.fg,
            tpt: val.tpt,
            ft: val.ft,
            to: val.to
        });

        });
    }
});


class StatsTable extends React.Component {
    constructor(props) {
        super(props);
    }

    componentDidMount() {

    }

    render() {
        console.log(players)
        const columns = [
            {
                Header: "PLAYER",
                accessor: "name"
            },
//            {
//                Header: "RTG",
//                accessor: "rating"
//            },
            {
                Header: "PPG",
                accessor: "ppg"
            },
            {
                Header: "REB",
                accessor: "reb"
            },
            {
                Header: "STL",
                accessor: "stl"
            },
            {
                Header: "BLK",
                accessor: "blk"
            },
            {
                Header: "FG%",
                accessor: "fg"
            },
            {
                Header: "3PT%",
                accessor: "tpt"
            },
            {
                Header: "FT%",
                accessor: "ft"
            },
            {
                Header: "TO",
                accessor: "to"
            }
        ]

        return (
                <ReactTable id="react-table" 
                    columns={columns}
                    data={players}/>
        )
    }
}


export default StatsTable
