import React from "react"
import scatter from "../images/scatter.png"
import pie from "../images/pie.png"
import bar from "../images/bar.png"
import line from "../images/line.png"

export default class GraphOptions extends React.Component {
    constructor(props) {
        super(props);

        this.graphStyle = scatterStyle;

        this.handleClick = this.handleClick.bind(this);
    }

    handleClick(e) {
        e.persist();
        let value = e.target.value;
        this.graphStyle = value;
        this.forceUpdate();
    }

    render() {
        return (
            <div id="graphOptions">
            <div id="graphs">
                <button style="background: url(../images/line.png)" value = "lineStyle" onClick={this.handleClick.bind(this)} />
                <button style="background: url(../images/bar.png)" value = "barStyle" onClick={this.handleClick.bind(this)} />
                <button style="background: url(../images/scatter.png)" value = "scatterStyle" onClick={this.handleClick.bind(this)} />
                <button style="background: url(../images/pie.png)" value = "pieStyle" onClick={this.handleClick.bind(this)} />
            </div>
            <div id="graphDescription">
            <h2>Scatter Plot</h2>
            <p>Two variables are plotted along two axes, the pattern of the resulting points reveal any correlations and outliers present.</p>
            </div>
        </div>
        )
    } 
}
