import React from "react"
import ReactTable from "react-table";
import Firebase from "./firebase"
import "react-table/react-table.css"

require("firebase");

const firebase_client = new Firebase();

let player_names = firebase_client.db.ref("players");


var names = ["James Harden", "Russel Westbrook", "Anthony Davis", "LeBron James", "Victor Oladipo", "Paul George", "Ben Simmons", "John Wall"]
var players = [];


class player {
constructor() {
this.name = "";
this.points = 0;
this.rebounds = 0;
this.assists = 0;
this.steals = 0;
this.blocks = 0;
this.fieldGoalPercentage = 0;
this.threePointPercentage = 0;
this.freeThrowPercentage = 0;
this.turnovers = 0;
} 
}


player_names.on('value', (snapshot) => {
    if(snapshot.exists()) {
        snapshot.forEach((data) => {
        var val = data.val();
        console.log(val);
        var new_player = new player();

        new_player.name = val.name;
        new_player.points = val.ppg;
        new_player.rebounds = val.reb;
        new_player.assists = val.ast;
        //console.log(new_player.assists)
        new_player.steals = val.stl;
        new_player.blocks = val.blk;
        new_player.fieldGoalPercentage = val.fg;
        new_player.threePointPercentage = val.tpt;
        new_player.freeThrowPercentage = val.ft;
        new_player.turnovers = val.to;

        players.push(new_player);

        });
    }
});



function addPlayerData() {
    for (var i = 0; i < 8; i++) {
        players.push({
            player: names[i],
            rating: Math.floor((Math.random() * 10) + 80),
            rebounds: Math.floor((Math.random() * 11) + 1),
            points: Math.floor((Math.random() * 15) + 20),
            assists: Math.floor((Math.random() * 10) + 2),
            steals: Math.floor((Math.random() * 3) + 0),
            blocks: Math.floor((Math.random() * 2) + 1),
            fieldgoal: .486,
            threepoint: .446,
            freethrow: 86.6,
            turnovers: Math.floor((Math.random() * 4) + 0)

        })
    };
}

addPlayerData();

class StatsTable extends React.Component {
    constructor(props) {
        super(props);
    }

    render() {
        const columns = [
            {
                Header: "PLAYER",
                accessor: "player"
            },
            {
                Header: "RTG",
                accessor: "rating"
            },
            {
                Header: "PPG",
                accessor: "points"
            },
            {
                Header: "REB",
                accessor: "rebounds"
            },
            {
                Header: "STL",
                accessor: "steals"
            },
            {
                Header: "BLK",
                accessor: "blocks"
            },
            {
                Header: "FG%",
                accessor: "fieldgoal"
            },
            {
                Header: "3PT%",
                accessor: "threepoint"
            },
            {
                Header: "FT%",
                accessor: "freethrow"
            },
            {
                Header: "TO",
                accessor: "turnovers"
            }
        ]

        return (
                <ReactTable id="react-table" 
                    columns={columns}
                    data={players}/>
        )
    }
}


export default StatsTable
